import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {
  const [enteredTitle, setenteredTitle] = useState("");
  const [enteredAmount, setenteredAmount] = useState("");
  const [enteredDate, setenteredDate] = useState("");
  //   const [userInput, setUserInput] = useState({
  //     enteredTitle: "",
  //     enteredAmount: "",
  //     enteredDate: "",
  //   });

  const titleChangeHandler = (event) => {
    setenteredTitle(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setenteredAmount(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setenteredDate(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      id: Math.random().toString(),
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onExpenseForm(expenseData);

    setenteredTitle("");
    setenteredAmount("");
    setenteredDate("");
  };

  const [isAddNewExpense, setIsAddNewExpense] = useState(false);

  const onAddNewExpenseHandler = () => {
    setIsAddNewExpense(true);
  };

  const onAddExpenseHandler = () => {
    setIsAddNewExpense(false);
  }

  return (
    <form id="ExpenseForm" onSubmit={submitHandler}>
    {isAddNewExpense && (
      <div className="new-expense__controls">
          <div className="new-expense__control">
            <label>Title</label>
            <input
              type="text"
              value={enteredTitle}
              onChange={titleChangeHandler}
            />
          </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            value={enteredAmount}
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2021-01-01"
            max="2023-12-31"
            value={enteredDate}
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      )}
      {isAddNewExpense && (
      <div className="new-expense__actions">
        <button type="submit">Cancel</button>
        <button type="submit" onClick={onAddExpenseHandler}>Add Expense</button>
      </div>
      )}
      <div>
        <button type="submit" onClick={onAddNewExpenseHandler}>
          Add New Expense
        </button>
      </div>
    </form>
  );
};

export default ExpenseForm;
