import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react";

function Expenses(props) {
  const [selectedYear, setSelectedYear] = useState("2020");

  const addExpenseFilterHandler = (year) => {
    setSelectedYear(year);
  };

  console.log(selectedYear);

  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          onExpenseFilter={addExpenseFilterHandler}
          selected={selectedYear}
        />
        {props.items
          .filter((expense) => expense.date().year.includes({ selectedYear }))
          .map((filterdExpense) => (
            <ExpenseItem
              key={filterdExpense.id}
              title={filterdExpense.title}
              amount={filterdExpense.amount}
              date={filterdExpense.date}
            />
          ))}
        {props.items.map((filterdExpense) => (
          <ExpenseItem
            key={filterdExpense.id}
            title={filterdExpense.title}
            amount={filterdExpense.amount}
            date={filterdExpense.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
