import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
import { useState } from "react";

function Expenses(props) {
  const [selectedYear, setSelectedYear] = useState("2020");

  const addExpenseFilterHandler = (year) => {
    setSelectedYear(year);
  }; 

  const filteredExpense = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === selectedYear;
  });

  let expenseContent = <p>No Expense Found.</p>

  if (filteredExpense > 0) {
  expenseContent = filteredExpense.map((filterdExpense) => (
    <ExpenseItem
      key={filterdExpense.id}
      title={filterdExpense.title}
      amount={filterdExpense.amount}
      date={filterdExpense.date}
    />
  ));
  }

  return (
    <div>
      <Card className="expenses">
        <ExpenseFilter
          onExpenseFilter={addExpenseFilterHandler}
          selected={selectedYear}
        />
        {expenseContent}
      </Card>
    </div>
  );
}

export default Expenses;
