import "../Experiment/css/base.css";

import Card from "../UI/Card";

function Concepts({ image, title, desc }) {
  return (
    <Card className="concept">
      <img src={image} alt={image} />
      <h2>{title}</h2>
      <p>{desc}</p>
      <div className="card">
        <div className="card__img" style={{ backgroundImage: `url(${image})` }}>
          <img src={image} alt={image} />
        </div>
        <div className="card__box card__box--c">
          <span className="card__box-number">02</span>
          <span className="card__box-tags">{title}</span>
        </div>
        <div className="card__box card__box--d">
          <span className="card__box-category">{desc}</span>
        </div>
      </div>
    </Card>
  );  
}

export default Concepts;
